surveyDefinition:
{
 "pages": [
  {
   "category": "Able to Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "ableToWork",
     "tag": "Optimum RadioGroup",
     "title": "Were you physically and/or mentally able to work during the week claimed?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "name": "ableToWorkExplanation ",
     "visible": false,
     "visibleIf": "{ableToWork} = 'No'",
     "tag": "Optimum MultipleLine",
     "title": "Provide an explanation as to why you were unavailable to work 10/14/2018. Please explain in detail.",
     "isRequired": true
    }
   ],
   "name": "Able to Work",
   "title": "Able to work"
  },
  {
   "category": "Available For Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "availableForWork",
     "tag": "Optimum RadioGroup",
     "title": "Other than a health issue,were you available for work during the week claimed?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "name": "availableForWorkExplanation",
     "visible": false,
     "visibleIf": "{availableForWork}='No'",
     "tag": "Optimum MultipleLine",
     "title": "Provide an explanation as to why you were unavailable to work 10/14/2018. Please explain in detail. (You may enter up to 256 characters)",
     "isRequired": true
    }
   ],
   "name": "Available For Work",
   "title": "Available For Work"
  },
  {
   "category": "Actively Seeking Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "furloughed",
     "tag": "Optimum RadioGroup",
     "title": "Are you furloughed due to federal shutdown?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "activelySeekingWork",
     "tag": "Optimum RadioGroup",
     "title": "Did you look for work during the week claimed?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "name": "activelySeekingWorkExplanation",
     "visible": false,
     "visibleIf": "({furloughed}='No' and {activelySeekingWork} ='No')",
     "tag": "Optimum MultipleLine",
     "title": "Provide an explanation as to why you were unable to actively seek work 10/14/2018. Please explain in detail.(You may enter up to 256 characters) ",
     "isRequired": true
    }
   ],
   "name": "Actively Seeking Work",
   "title": "Actively Seeking Work"
  },
  {
   "category": "Actively Seeking Work",
   "elements": [
    {
     "type": "panel",
     "name": "jobSearchPanel1",
     "elements": [
      {
       "type": "dropdown",
       "choices": [
        "11/04/2018",
        "11/05/2018",
        "11/06/2018",
        "11/07/2018",
        "11/08/2018",
        "11/09/2018",
        "11/10/2018"
       ],
       "isRequired": true,
       "name": "jobSearchDateOfContact",
       "tag": "Optimum Dropdown",
       "title": "Date of Job Contact(First of Two)"
      },
      {
       "type": "checkbox",
       "name": "jobSearchContactMade1",
       "tag": "Optimum Checkbox",
       "title": "How Contact Was Made(Check all that apply)",
       "isRequired": true,
       "choices": [
        "Internet",
        "Phone",
        "Mail",
        "In Person"
       ]
      },
      {
       "type": "text",
       "name": "jobSearchEmployerName",
       "tag": "Optimum SingleLine",
       "title": "Name:"
      },
      {
       "type": "multipletext",
       "name": "jobSearchEmployerAddress",
       "tag": "Optimum Address",
       "title": "Employer Address",
       "isRequired": true,
       "items": [
        {
         "name": "addressLine1"
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city"
        },
        {
         "name": "state"
        },
        {
         "name": "zip"
        },
        {
         "name": "zipExtension"
        }
       ]
      },
      {
       "type": "text",
       "name": "jobSearchemployerPhone",
       "tag": "Optimum Phone",
       "title": "Employer Phone:",
       "isRequired": true,
       "inputType": "tel",
       "size": 10
      },
      {
       "type": "text",
       "name": "jobSearchPositionAppliedFor",
       "tag": "Optimum SingleLine",
       "title": "Position Applied For:",
       "isRequired": true
      },
      {
       "type": "checkbox",
       "name": "resultsJobSearch1",
       "tag": "Optimum Checkbox",
       "title": "Results(Check all that apply)",
       "choices": [
        "Interviewed",
        "Follow up Scheduled by Employer",
        "Follow up Promised by Employer",
        "Follow up Requested by Employer",
        "Hired",
        "Employer Not Hiring"
       ]
      }
     ],
     "visible": false,
     "visibleIf": "({furloughed}='No' and {activelySeekingWork} ='Yes')"
    },
    {
     "type": "panel",
     "name": "jobSearchPanel2",
     "elements": [
      {
       "type": "dropdown",
       "choices": [
        "11/04/2018",
        "11/05/2018",
        "11/06/2018",
        "11/07/2018",
        "11/08/2018",
        "11/09/2018",
        "11/10/2018"
       ],
       "isRequired": true,
       "name": "jobSearchDateofContact2",
       "tag": "Optimum Dropdown",
       "title": "Date of Job Contact(Second of Two)"
      },
      {
       "type": "checkbox",
       "name": "jobSearchContactMade2",
       "tag": "Optimum Checkbox",
       "title": "How Contact Was Made(Check all that apply)",
       "isRequired": true,
       "choices": [
        "Internet",
        "Phone",
        "Mail",
        "In Person"
       ]
      },
      {
       "type": "text",
       "name": "jobSearchName2",
       "tag": "Optimum SingleLine",
       "title": "Name:"
      },
      {
       "type": "multipletext",
       "name": "jobSearchAddress2",
       "tag": "Optimum Address",
       "title": "Employer Address:",
       "isRequired": true,
       "items": [
        {
         "name": "addressLine1"
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city"
        },
        {
         "name": "state"
        },
        {
         "name": "zip"
        },
        {
         "name": "zipExtension"
        }
       ]
      },
      {
       "type": "text",
       "name": "jobSearchPhone2",
       "tag": "Optimum Phone",
       "title": "Phone:",
       "isRequired": true,
       "inputType": "tel"
      },
      {
       "type": "text",
       "name": "jobSearchPosition2",
       "tag": "Optimum SingleLine",
       "title": "Position Applied For:",
       "isRequired": true
      },
      {
       "type": "checkbox",
       "name": "resultsJobSearch2",
       "tag": "Optimum Checkbox",
       "title": "Results(Check all that apply)",
       "isRequired": true,
       "choices": [
        "Interviewed",
        "Follow up Scheduled by Employer",
        "Follow up Promised by Employer",
        "Follow up Requested by Employer",
        "Hired",
        "Employer Not Hiring"
       ]
      }
     ],
     "visible": false,
     "visibleIf": "({furloughed}='No' and {activelySeekingWork} ='Yes')"
    }
   ],
   "name": "Job Search",
   "title": "Job Search",
   "visible": false,
   "visibleIf": "({furloughed}='No' and {activelySeekingWork} ='Yes')"
  },
  {
   "category": "Perform Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "performWork",
     "tag": "Optimum RadioGroup",
     "title": "Did you perform work during the week claimed? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "performWorkPanel",
     "elements": [
      {
       "type": "radiogroup",
       "name": "performWorkStillEmployed",
       "width": "50",
       "tag": "Optimum RadioGroup",
       "title": "Are you still working for this employer?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "text",
       "name": "performToWorkHoursWorked",
       "tag": "Optimum Number",
       "title": "Number of Hours Worked This Week:",
       "isRequired": true
      },
      {
       "type": "text",
       "name": "performWorkWeeklyAmount",
       "tag": "Optimum Currency",
       "title": "Gross Weekly Amount Earned:",
       "isRequired": true,
       "placeHolder": "$ 1,000,000"
      },
      {
       "type": "text",
       "name": "performWorkEmployerName",
       "tag": "Optimum SingleLine",
       "title": "Employer Name",
       "isRequired": true
      },
      {
       "type": "multipletext",
       "name": "performWorkEmploymentDates",
       "tag": "Optimum StartEndDatepicker",
       "startDateName": "Enter Employment Start Date",
       "endDateName": "Enter Employment End Date ",
       "title": "Enter Employment Dates:",
       "items": [
        {
         "name": "startDate",
         "title": "Enter Employment Start Date"
        },
        {
         "name": "endDate",
         "title": "Enter Employment End Date"
        }
       ]
      },
      {
       "type": "multipletext",
       "name": "performWorkEmployerAddress",
       "tag": "Optimum Address",
       "title": "Employer Address",
       "isRequired": true,
       "items": [
        {
         "name": "addressLine1"
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city"
        },
        {
         "name": "state"
        },
        {
         "name": "zip"
        },
        {
         "name": "zipExtension"
        }
       ]
      }
     ],
     "visible": false,
     "visibleIf": "{performWork}='Yes'"
    }
   ],
   "name": "Perform Work",
   "title": "Perform Work"
  },
  {
   "category": "Severance Pay ",
   "elements": [
    {
     "type": "radiogroup",
     "name": "severancePayQuestion",
     "tag": "Optimum RadioGroup",
     "title": "Did you begin receiving a severance or did the amount previously reported change?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "severancePayPanel",
     "elements": [
      {
       "type": "radiogroup",
       "name": "severancePayIsLumpSump",
       "tag": "Optimum RadioGroup",
       "title": "Was the Severance Payment a Lump Sum?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "text",
       "name": "severancePayReceiveDate",
       "visible": false,
       "visibleIf": "{severancePayIsLumpSump} ='Yes'",
       "tag": "Optimum Date",
       "title": "Enter Severance Receive Date ",
       "isRequired": true
      },
      {
       "type": "text",
       "name": "severancePayGrossPay",
       "tag": "Optimum Currency",
       "title": "Gross Severance Pay",
       "isRequired": true,
       "placeHolder": "$ 1,000,000"
      }
     ],
     "visible": false,
     "visibleIf": "{severancePayQuestion}='Yes'"
    }
   ],
   "name": "Severance Pay ",
   "title": "Severance Pay "
  },
  {
   "category": "Pension",
   "elements": [
    {
     "type": "radiogroup",
     "name": "pension",
     "tag": "Optimum RadioGroup",
     "title": "Did you begin receiving a pension or did the amount previously reported change?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "pensionPanel",
     "elements": [
      {
       "type": "text",
       "name": "pensionEmployer",
       "tag": "Optimum SingleLine",
       "title": "Name of the Employer:",
       "isRequired": true
      },
      {
       "type": "text",
       "name": "pensionAmount",
       "tag": "Optimum Currency",
       "title": "Monthly Pension Amount:",
       "isRequired": true,
       "placeHolder": "$ 1,000,000"
      },
      {
       "type": "radiogroup",
       "name": "pensionContribution",
       "tag": "Optimum RadioGroup",
       "title": "Did your employer contribute 100% of the pension?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "text",
       "name": "pensionEffectiveDate",
       "tag": "Optimum Date",
       "title": "Enter Pension Effective Date:",
       "isRequired": true
      }
     ],
     "visible": false,
     "visibleIf": "{pension}='Yes'"
    }
   ],
   "name": "Pension",
   "title": "Pension"
  },
  {
   "category": "Training ",
   "elements": [
    {
     "type": "radiogroup",
     "name": "training",
     "tag": "Optimum RadioGroup",
     "title": "Did you begin school/training or has there been a change in your schedule  during the week claimed?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "trainingPanel",
     "elements": [
      {
       "type": "text",
       "name": "trainingSchoolName",
       "tag": "Optimum SingleLine",
       "title": "Name of Training/School:",
       "isRequired": true
      },
      {
       "type": "radiogroup",
       "name": "trainingApproved",
       "tag": "Optimum RadioGroup",
       "title": "Is this training/schooling approved by DOES Agency?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "text",
       "name": "pensionExplanation",
       "tag": "Optimum MultipleLine",
       "title": "(You may enter up to 256 characters)"
      }
     ],
     "visible": false,
     "visibleIf": "{training} ='Yes'"
    }
   ],
   "name": "Training ",
   "title": "Training"
  },
  {
   "category": "Refuse Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "refuseWork",
     "tag": "Optimum RadioGroup",
     "title": "Did you refuse work,quit a job, or were you discharged from a job during the week claimed?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "refuseWorkPanel",
     "elements": [
      {
       "type": "text",
       "name": "refuseEmployerName",
       "tag": "Optimum SingleLine",
       "title": "Employer Name:",
       "isRequired": true
      },
      {
       "type": "multipletext",
       "name": "pensionEmployerAddress",
       "tag": "Optimum Address",
       "title": "Employer Address",
       "isRequired": true,
       "items": [
        {
         "name": "addressLine1"
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city"
        },
        {
         "name": "state"
        },
        {
         "name": "zip"
        },
        {
         "name": "zipExtension"
        }
       ]
      },
      {
       "type": "text",
       "name": "pensionExplanation ",
       "tag": "Optimum MultipleLine",
       "title": "You responded that you refused work, quit a job or were discharged from a job during the week ending 10/14/2017. Please explain in detail.",
       "isRequired": true
      }
     ],
     "visible": false,
     "visibleIf": "{refuseWork}='Yes'"
    }
   ],
   "name": "Refuse Work",
   "title": "Refuse Work"
  },
  {
   "category": "Returned To Work",
   "elements": [
    {
     "type": "radiogroup",
     "name": "returnToWorkFullTime",
     "tag": "Optimum RadioGroup",
     "title": "Did you return to full-time work?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "panel1",
     "elements": [
      {
       "type": "dropdown",
       "choices": [
        "11/04/2018",
        "11/05/2018",
        "11/06/2018",
        "11/07/2018",
        "11/08/2018",
        "11/09/2018",
        "11/10/2018"
       ],
       "isRequired": true,
       "name": "returnedtoWorkDate",
       "tag": "Optimum Dropdown",
       "title": "Date Returned to Full-time Work:"
      },
      {
       "type": "text",
       "name": "returnedToWorkAmount",
       "tag": "Optimum Currency",
       "title": "Amount Earned During This Week",
       "isRequired": true,
       "placeHolder": "$ 1,000,000"
      },
      {
       "type": "text",
       "name": "returnedToWorkEmployerName",
       "tag": "Optimum SingleLine",
       "title": "Employer Name:",
       "isRequired": true
      },
      {
       "type": "multipletext",
       "name": "returnedToWorkEmployerAddress",
       "tag": "Optimum Address",
       "title": "Employer Address:",
       "isRequired": true,
       "items": [
        {
         "name": "addressLine1"
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city"
        },
        {
         "name": "state"
        },
        {
         "name": "zip"
        },
        {
         "name": "zipExtension"
        }
       ]
      },
      {
       "type": "text",
       "name": "returnedToWorkEmployerPhone",
       "tag": "Optimum Phone",
       "title": "Phone No:",
       "isRequired": true,
       "inputType": "tel"
      }
     ],
     "visible": false,
     "visibleIf": "{returnToWorkFullTime}='Yes'"
    }
   ],
   "name": "Returned To Work",
   "title": "Returned To Work"
  }
 ],
 "storeOthersAsComment": false
}
