  {
   "category": "General Info",
   "elements": [
    {
     "type": "radiogroup",
     "name": "filedOutOfStateUiBenefits",
     "tag": "Optimum RadioGroup",
     "title": "Have you filed for unemployment insurance benefits in any state other than Iowa since <<basePeriodBeginDate>>? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    }
   ],
   "name": "Existing Claim",
   "title": "Existing Claim"
  },
  {
   "category": "Employment Info",
   "elements": [
    {
     "type": "radiogroup",
     "name": "unionHiringHall",
     "tag": "Optimum RadioGroup",
     "helpText": "An employment office operated by a union for placing members in jobs.",
