surveyDefinition:
{
 "pages": [
  {
   "category": "Subsequent Employment",
   "elements": [
    {
     "type": "radiogroup",
     "name": "subsequentEmployment",
     "tag": "Optimum RadioGroup",
     "title": "Have you worked since  <<basePeriodBeginDate>>? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    }
   ],
   "name": "subsequentEmployment",
   "title": "Subsequent Employment"
  },
  {
   "category": "Work Authorization",
   "elements": [
    {
     "type": "radiogroup",
     "name": "citizenshipConfirmation",
     "tag": "Optimum RadioGroup",
     "title": {
      "default": "Are you a citizen of the United States?",
      "es": "[ES] Are you a citizen of the United States?"
     },
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "notACitizenPanel",
     "elements": [
      {
       "type": "dropdown",
       "name": "nonCitizenWorkDocuments",
       "tag": "Optimum Dropdown",
       "title": {
        "default": "Please select the document that confirms you are approved to work in the United States.",
        "es": "[ES] Please select the document that confirms you are approved to work in the United States."
       },
       "isRequired": true,
       "choices": [
        {
         "value": "Permanent Resident Card I-551",
         "text": "Permanent Resident Card I-551"
        },
        {
         "value": "Employment Authorization Document Form I-766",
         "text": "Employment Authorization Document Form I-766"
        },
        {
         "value": "Arrival Departure Record Form I-94",
         "text": "Arrival Departure Record Form I-94"
        },
        {
         "value": "Refugee Travel Document Form I-571",
         "text": "Refugee Travel Document Form I-571"
        },
        {
         "value": "Reentry Permit Form I-327",
         "text": "Reentry Permit Form I-327"
        },
        {
         "value": "Foreign Passport",
         "text": "Foreign Passport"
        },
        {
         "value": "Other",
         "text": "Other"
        }
       ]
      },
      {
       "type": "panel",
       "name": "panel",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "minlength": 9,
         "name": "permanentResidentCardI55AlienUscisNumber",
         "size": 9,
         "tag": "Optimum ConfirmInput",
         "title": "Alien/USCIS Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "isRequired": true,
         "minlength": 13,
         "name": "permanentResidentCardI55CardNumber",
         "size": 13,
         "tag": "Optimum ConfirmInput",
         "title": "Card Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "permanentResidentCardI55DocExpirationDate",
         "tag": "Optimum Date",
         "title": {
          "default": "Document Expiration Date",
          "es": "[ES] Document Expiration Date"
         }
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Permanent Resident Card I-551'"
      },
      {
       "type": "panel",
       "name": "panel6",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "minlength": 9,
         "name": "employmentAuthorizationDocumentFormI766AlienUscisNumber",
         "size": 9,
         "tag": "Optimum ConfirmInput",
         "title": "Alien/USCIS Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "isRequired": true,
         "minlength": 13,
         "name": "employmentAuthorizationDocumentFormI766CardNumber",
         "size": 13,
         "tag": "Optimum ConfirmInput",
         "title": "Card Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "employmentAuthorizationDocumentFormI766DocExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Employment Authorization Document Form I-766'"
      },
      {
       "type": "panel",
       "name": "panel5",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "minlength": 11,
         "name": "arrivalDepartureRecordFormI94Number",
         "size": 11,
         "tag": "Optimum ConfirmNumber",
         "title": "I-94 Number (Admission Number)",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "arrivalDepartureRecordFormI94DocExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        },
        {
         "type": "text",
         "minlength": 10,
         "name": "sevisId",
         "size": 10,
         "tag": "Optimum Number",
         "title": "SEVIS ID"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Arrival Departure Record Form I-94'"
      },
      {
       "type": "panel",
       "name": "panel4",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "minlength": 9,
         "name": "refugeeTravelDocumentFormI571AlienUscisNumber",
         "size": 9,
         "tag": "Optimum ConfirmInput",
         "title": "Alien/USCIS Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "refugeeTravelDocumentFormI571DocExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Refugee Travel Document Form I-571'"
      },
      {
       "type": "panel",
       "name": "panel3",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "minlength": 9,
         "name": "reentryPermitFormI327AlienUscisNumber",
         "size": 9,
         "tag": "Optimum ConfirmInput",
         "title": "Alien/USCIS Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "reentryPermitFormI327DocExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Reentry Permit Form I-327'"
      },
      {
       "type": "panel",
       "name": "panel2",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "name": "passportNumber",
         "size": 20,
         "tag": "Optimum ConfirmInput",
         "title": "Passport Number",
         "titleLocation": "hidden"
        },
        {
         "type": "dropdown",
         "name": "countryOfPassportIssuance",
         "tag": "Optimum Dropdown",
         "title": "Country of Issuance",
         "isRequired": true,
         "choiceUrl": "optimum-clientside-library/platform-services/dist/data/countryList.json"
        },
        {
         "type": "text",
         "isRequired": true,
         "name": "passportDocumentExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Foreign Passport'"
      },
      {
       "type": "panel",
       "name": "panel1",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "name": "otherDocumentName",
         "tag": "Optimum SingleLine",
         "title": "Document Name"
        },
        {
         "type": "text",
         "isRequired": true,
         "minlength": 9,
         "name": "otherAlienUscisNumber",
         "size": 9,
         "tag": "Optimum ConfirmInput",
         "title": "Alien/USCIS Number",
         "titleLocation": "hidden"
        },
        {
         "type": "text",
         "name": "otherDocExpirationDate",
         "tag": "Optimum Date",
         "title": "Document Expiration Date"
        }
       ],
       "visible": false,
       "visibleIf": "{nonCitizenWorkDocuments} contains 'Other'"
      }
     ],
     "visible": false,
     "visibleIf": "{citizenshipConfirmation} = 'No'"
    }
   ],
   "name": "Work Authorization",
   "title": "Work Authorization"
  },
  {
   "category": "Employment Info",
   "elements": [
    {
     "type": "radiogroup",
     "name": "unionHiringHall",
     "tag": "Optimum RadioGroup",
     "helpText": "An employment office operated by a union for placing members in jobs.",
     "title": "Do you receive work through a union hiring hall?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "panel5",
     "elements": [
      {
       "type": "radiogroup",
       "name": "unionStanding",
       "tag": "Optimum RadioGroup",
       "title": "Are you a member in good standing?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "panel",
       "name": "panel6",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "name": "unionName",
         "tag": "Optimum SingleLine",
         "title": "Please enter your union name"
        },
        {
         "type": "text",
         "isRequired": true,
         "name": "unionLocalNumber",
         "tag": "Optimum Number",
         "title": "Please enter your union local number"
        }
       ],
       "visible": false,
       "visibleIf": "{unionStanding}='Yes'"
      }
     ],
     "visible": false,
     "visibleIf": "{unionHiringHall}='Yes'"
    }
   ],
   "name": "Union",
   "title": "Union",
   "visible": false,
   "visibleIf": "{subsequentEmployment} = 'Yes'"
  },
  {
   "blockIf": "(compareDate(getEndDate({militaryService}), \"<<effectiveDate>>\") = 1)",
   "category": "Employment Info",
   "elements": [
    {
     "type": "radiogroup",
     "name": "militaryServiceOnActiveDuty",
     "tag": "Optimum RadioGroup",
     "title": "Was your most recent employment with the U.S. military? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "panel7",
     "elements": [
      {
       "type": "panel",
       "name": "panel8",
       "elements": [
        {
         "type": "html",
         "name": "question1",
         "html": "Additional information is needed to complete your claim. Use your Certificate of Release or Discharge from Active Duty DD214 to answer the following questions then upload your Member 4 copy of the DD214."
        },
        {
         "type": "text",
         "isRequired": true,
         "name": "militaryServiceBranchOfService",
         "tag": "Optimum SingleLine",
         "title": "What was your Branch of Service? (Item 2)"
        },
        {
         "type": "text",
         "isRequired": true,
         "name": "militaryServiceLastPayGrade",
         "tag": "Optimum SingleLine",
         "title": "What was your last pay grade? (Item 4b)"
        },
        {
         "type": "multipletext",
         "endDateName": "What was your Separation Date? (Item 12b)",
         "isLimitToCurrentDate": true,
         "isRequired": true,
         "items": [
          {
           "name": "startDate"
          },
          {
           "name": "endDate"
          }
         ],
         "name": "militaryService",
         "questionMetadata": "{\"startDateIsAboveMinDateErrorMsg\": \"Date cannot be before the start date of time lost.\", \"startDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\",\"endDateIsAboveMinDateErrorMsg\": \"Separation date cannot be before your entry date.\", \"endDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\"}",
         "startDateName": "What was your Entry Date? (Item 12a)",
         "tag": "Optimum StartEndDatepicker",
         "title": "Please enter your entry date and separation date",
         "titleLocation": "hidden"
        },
        {
         "type": "html",
         "name": "militaryServiceCantSubmitMsg",
         "visible": false,
         "visibleIf": "compareDate(getEndDate({militaryService}), computeBYB()) = 1",
         "dynamicTitle": "If you submit this application today, the start date of your UI claim is Sunday, <<effectiveDate>>. Federal law does not allow a Military UI claim to start before the separation date listed on the DD214. You must wait to submit your claim application until Sunday, <<sundayAfterEffectiveDate>>. \n",
         "html": "If you submit this application today, the start date of your UI claim is Sunday, <<effectiveDate>>. Federal law does not allow a Military UI claim to start before the separation date listed on the DD214. You must wait to submit your claim application until Sunday, <<sundayAfterEffectiveDate>>. \n"
        },
        {
         "type": "panel",
         "name": "panel9",
         "elements": [
          {
           "type": "text",
           "isRequired": true,
           "name": "militaryServiceDaysOfAccruedLeave",
           "tag": "Optimum Number",
           "title": "Please enter the number of days of accrued leave as it appears in item 16 on Form DD214"
          },
          {
           "type": "dropdown",
           "name": "militaryServiceCharacter",
           "tag": "Optimum Dropdown",
           "title": "What was the Character of Service? (Item 24)",
           "isRequired": true,
           "choices": [
            "Honorable",
            "Dishonorable",
            "General",
            "Other Than Honorable",
            "Uncharacterized",
            "Bad Conduct"
           ]
          },
          {
           "type": "radiogroup",
           "name": "militaryServiceDatesOfTimeLost",
           "tag": "Optimum RadioGroup",
           "title": "Are there any dates of time lost listed in item #29 on your DD214?",
           "isRequired": true,
           "choices": [
            "Yes",
            "No"
           ]
          },
          {
           "type": "text",
           "name": "militaryServiceTimeLostDatesLabel",
           "placeHolder": "THIS IS NOT A QUESTION",
           "tag": "Optimum Label",
           "title": "Please enter the Start Date and End Date of time lost in item #29 on your DD214.",
           "visible": false,
           "visibleIf": "{militaryServiceDatesOfTimeLost} = 'Yes'"
          },
          {
           "type": "multipletext",
           "endDateName": "End Date",
           "isLimitToCurrentDate": true,
           "isRequired": true,
           "name": "militaryServiceTimeLostDates",
           "questionMetadata": "{\"startDateIsAboveMinDateErrorMsg\": \"Date cannot be before the start date of time lost.\", \"startDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\",\"endDateIsAboveMinDateErrorMsg\": \"Date cannot be before the start date of time lost.\", \"endDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\"}",
           "startDateName": "Start Date",
           "tag": "Optimum StartEndDatepicker",
           "title": "Please enter the Start Date and End Date of time lost in item #29 on your DD214.",
           "titleLocation": "hidden",
           "visible": false,
           "visibleIf": "{militaryServiceDatesOfTimeLost} = 'Yes'"
          },
          {
           "type": "radiogroup",
           "name": "militaryServiceOtherEmployment",
           "visible": false,
           "visibleIf": "{militaryServiceDatesOfTimeLost} = 'No'",
           "tag": "Optimum RadioGroup",
           "dynamicTitle": "Have you had any other employment since your military service ended on getEndDate({militaryService})?",
           "title": "Have you had any other employment since your military service ended on getEndDate({militaryService})?",
           "isRequired": true,
           "choices": [
            "Yes",
            "No"
           ]
          },
          {
           "type": "panel",
           "name": "panel10",
           "elements": [
            {
             "type": "text",
             "isRequired": true,
             "name": "militaryServiceJobTitle",
             "tag": "Optimum JobSearch",
             "title": "What was your job title?"
            },
            {
             "type": "text",
             "isRequired": true,
             "name": "militaryServiceJobDuties",
             "tag": "Optimum SingleLine",
             "title": "What were your job duties?"
            }
           ],
           "visible": false,
           "visibleIf": "{militaryServiceOtherEmployment} ='No'"
          }
         ],
         "visible": false,
         "visibleIf": "(compareDate(getEndDate({militaryService}), computeBYB()) < 0) or (compareDate(getEndDate({militaryService}), computeBYB()) = 0)"
        }
       ],
       "visible": false,
       "visibleIf": "{militaryServiceOnActiveDuty} = 'Yes'"
      }
     ],
     "visible": false,
     "visibleIf": "{militaryServiceOnActiveDuty} = 'Yes'"
    }
   ],
   "name": "Military",
   "title": "Military",
   "visible": false,
   "visibleIf": "{subsequentEmployment} = 'Yes'"
  },
  {
   "category": "Employment Info",
   "elements": [
    {
     "type": "radiogroup",
     "name": "federalWorkConfirmation",
     "tag": "Optimum RadioGroup",
     "title": "Did you work for the Federal Government (not including military service) at any time after <<basePeriodBeginDate>>?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "name": "panel11",
     "elements": [
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmployerName",
       "tag": "Optimum SingleLine",
       "title": "What was the name of your Federal Employer?"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalAgencyDepartment",
       "tag": "Optimum SingleLine",
       "title": "In which department within the Federal Agency did you work?"
      },
      {
       "type": "dropdown",
       "name": "federalLastOfficialDutyStation",
       "visible": false,
       "visibleIf": "{federalWorkConfirmation}='Yes'",
       "tag": "Optimum Dropdown",
       "title": "What was your Last Office Duty Station?",
       "isRequired": true,
       "choices": [
        "item1",
        "item2",
        "item3"
       ],
       "choiceUrl": "optimum-clientside-library/platform-services/dist/data/statesList.json"
      },
      {
       "type": "panel",
       "name": "panel12",
       "elements": [
        {
         "type": "multipletext",
         "isRequired": true,
         "items": [
          {
           "name": "addressLine1"
          },
          {
           "name": "addressLine2"
          },
          {
           "name": "city"
          },
          {
           "name": "state"
          },
          {
           "name": "zip"
          },
          {
           "name": "zipExtension"
          }
         ],
         "name": "federalPayrollAddress",
         "tag": "Optimum Address",
         "title": "What is the payroll address from your SF8 or SF50 form?"
        },
        {
         "type": "multipletext",
         "endDateName": "Last date worked",
         "isLimitToCurrentDate": true,
         "isRequired": true,
         "name": "federalEmploymentDates",
         "questionMetadata": "{\"startDateIsAboveMinDateErrorMsg\": \"Date cannot be before the start date of time lost.\", \"startDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\",\"endDateIsAboveMinDateErrorMsg\": \"Last date worked cannot be before the date you started your federal employment.\", \"endDateIsBelowMaxDateErrorMsg\":\"Date cannot be in the future.\"}",
         "startDateName": "First date worked",
         "tag": "Optimum StartEndDatepicker",
         "title": "What were your dates of employment?",
         "titleLocation": "hidden"
        },
        {
         "type": "radiogroup",
         "name": "federalOtherEmployment",
         "visible": false,
         "visibleIf": "{militaryServiceOnActiveDuty} = 'No' and getEndDate({federalEmploymentDates}) notempty",
         "tag": "Optimum RadioGroup",
         "dynamicTitle": "Have you had other employment since getEndDate({federalEmploymentDates})?",
         "title": "Have you had other employment since getEndDate({federalEmploymentDates})?",
         "isRequired": true,
         "choices": [
          "Yes",
          "No"
         ]
        },
        {
         "type": "panel",
         "name": "panel13",
         "elements": [
          {
           "type": "panel",
           "name": "panel14",
           "elements": [
            {
             "type": "text",
             "isRequired": true,
             "name": "federalOtherEmploymentJobTitle",
             "tag": "Optimum JobSearch",
             "title": "What was your job title?"
            },
            {
             "type": "radiogroup",
             "name": "federalOtherEmploymentWasFT",
             "tag": "Optimum RadioGroup",
             "title": "Was this full-time employment?",
             "isRequired": true,
             "choices": [
              "Yes",
              "No"
             ]
            },
            {
             "type": "radiogroup",
             "name": "federalOtherEmploymentFtEmployer",
             "visible": false,
             "visibleIf": "{federalOtherEmploymentWasFT}='No'",
             "tag": "Optimum RadioGroup",
             "title": "Have you worked full-time for any employer since <<basePeriodBeginDate>>?",
             "isRequired": true,
             "choices": [
              "Yes",
              "No"
             ]
            },
            {
             "type": "dropdown",
             "name": "federalReasonSeparation",
             "tag": "Optimum Dropdown",
             "helpText": "Reason for Separation <br><ul><li>I quit - You decided to leave employment for any reason except mandatory retirement, or for service in the armed forces.</li></ul>",
             "title": "What was your reason for separation?",
             "isRequired": true,
             "choices": [
              "I quit",
              "I was terminated",
              "I was laid off",
              {
               "value": "I'm still employed",
               "text": "Im still employed"
              },
              "I left for military service or training",
              {
               "value": "I'm off work due to a labor dispute/strike/lockout",
               "text": "Im off work due to a labor dispute/strike/lockout"
              },
              {
               "value": "I'm off work due to a natural disaster",
               "text": "Im off work due to a natural disaster"
              },
              "The business I worked for closed permanently"
             ]
            },
            {
             "type": "dropdown",
             "name": "federalReasonTermination",
             "visible": false,
             "visibleIf": "{federalReasonSeparation}='I was terminated'",
             "tag": "Optimum Dropdown",
             "helpText": "Reason for Termination <br><ul><li>Reduction in force or company downsizing - Your employer ended your employment due to lack of funds, lack of work, redesign or elimination of position(s) or reorganization, with no likelihood or expectation that the employee will be recalled because the position itself is eliminated.</li><li>Alleged misconduct - Your employer ended your employment due to a disregard for the employer&#x2019;s policies or standards. </li><li>Forced Resignation - Your employer ended your employment by requiring you to quit.  A forced resignation is often offered instead of termination.</li></ul>",
             "title": "What was the reason for your termination?",
             "isRequired": true,
             "choices": [
              "Reduction in force or company downsizing",
              "No reason given to me by my employer",
              "Temporary assignment ended/Employer had no more work available",
              "Work performance/Not meeting company goals",
              "Attendance",
              "Medical reasons",
              "Alleged misconduct ",
              "Alleged theft ",
              "Work rule violation ",
              "Forced Resignation ",
              "Other"
             ]
            },
            {
             "type": "dropdown",
             "name": "federalReasonLaidOff",
             "visible": false,
             "visibleIf": "{federalReasonSeparation}='I was laid off'",
             "tag": "Optimum Dropdown",
             "title": "Please select one of the following:",
             "isRequired": true,
             "choices": [
              "I am permanently laid off ",
              "I am temporarily laid off ",
              "My temporary job assignment ended "
             ]
            },
            {
             "type": "dropdown",
             "name": "federalReasonStillEmployed",
             "visible": false,
             "visibleIf": "{federalReasonSeparation}='Im still employed'",
             "tag": "Optimum Dropdown",
             "title": "Please select one of the following:",
             "isRequired": true,
             "choices": [
              "I requested a reduction in hours ",
              "Employer has permanently reduced my hours ",
              "Employer has temporarily reduced my hours ",
              "I am currently on a leave of absence ",
              "There has been no change to my hours with this employer "
             ]
            },
            {
             "type": "panel",
             "name": "panel15",
             "elements": [
              {
               "type": "dropdown",
               "name": "federalReasonClosedPermanently",
               "tag": "Optimum Dropdown",
               "title": "Please select one of the following:",
               "isRequired": true,
               "choices": [
                "Entire business closed",
                "Work site/location closed",
                "Department closed"
               ]
              },
              {
               "type": "radiogroup",
               "name": "federalClosedPermanentlySold",
               "tag": "Optimum RadioGroup",
               "title": "Was the employer's business sold?",
               "isRequired": true,
               "choices": [
                "Yes",
                "No"
               ]
              },
              {
               "type": "panel",
               "name": "panel16",
               "elements": [
                {
                 "type": "text",
                 "isLimitToCurrentDate": true,
                 "isRequired": true,
                 "name": "federalClosedPermanentlySoldDate",
                 "questionMetadata": "{\"isBelowMaxDateErrorMsg\": \"Date cannot be in the future.\"}",
                 "tag": "Optimum Date",
