surveyDefinition:
{
 "pages": [
  {
   "category": "Filing Eligibility",
   "elements": [
    {
     "type": "radiogroup",
     "name": "Federal Civilian Employment 1/3",
     "tag": "Optimum RadioGroup",
     "title": "Have you worked for the Federal Government or the U.S. Postal Service as a civilian since <<basePeriodBeginDate>> ? You will need an SF8 form or an SF50 form.",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Military Service 1/1",
     "tag": "Optimum RadioGroup",
     "title": "Have you had any active military service since <<basePeriodBeginDate>>?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Determine Eligibility 1/3",
     "tag": "Optimum RadioGroup",
     "title": "Did you work in Washington DC between the dates of <<basePeriodBeginDate>> and <<basePeriodEndDate>>?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Determine Eligibility 2/3",
     "tag": "Optimum RadioGroup",
     "title": "Are you filing this claim form within the District of Columbia?\n",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "inputType": "number",
     "isRequired": true,
     "name": "Determine Eligibility 3/3",
     "size": 5,
     "tag": "Optimum Number",
     "title": "What is your zip Code?"
    }
   ],
   "name": "Filing Eligibility",
   "title": "Filing Eligibility"
  },
  {
   "blockIf": "{Military Service 1/1} = 'Yes' or  {Determine Eligibility 1/3} = 'No'",
   "category": "Filing Eligibility",
   "elements": [
    {
     "type": "panel",
     "elements": [
      {
       "type": "html",
       "name": "question2",
       "html": "<center><h5>YOUR CLAIM CANNOT BE FILED OVER THE INTERNET</h5></center>\n\n<b>You cannot file an initial claim for Regular Unemployment Compensation over the internet: </b>"
      },
      {
       "type": "html",
       "name": "question1",
       "visible": false,
       "visibleIf": "{Military Service 1/1} = 'Yes' ",
       "html": "<ul>\n                 <li>You were recently in the military </li>\n </ul>"
      },
      {
       "type": "html",
       "name": "question8",
       "visible": false,
       "visibleIf": "({Federal Civilian Employment 1/3}= 'No' and  {Determine Eligibility 1/3} = 'No')",
       "html": "<ul>\n                 <li>You worked outside the District of Columbia </li>\n</ul>"
      },
      {
       "type": "html",
       "name": "question7",
       "html": "\n<b>If you are a District of Columbia resident,</b> you need to file your initial claim in person at one of our One Stop Centers. Claims are taken by appointment. To make an appointment, please call 724-7000. One Stop Centers which take initial claims are located at: \n          <ul>         \n                  <li>5171 South Dakota Avenue, N.E., 2nd Floor </li>\n                  <li>3720 Martin Luther King, Jr.Avenue, S.E. </li>\n          </ul>\n\n<b>If you are a Maryland resident,</b> you may file your unemployment claim against the District of Columbia through the Maryland Unemployment Office by phone. The number is 301-313-8000.\n</br></br>\n<b>If you live in any other state,</b> you should report to the nearest unemployment office in your state of residence to file a claim against the District of Columbia.\n"
      }
     ],
     "name": "UCX Claim Message",
     "visible": false,
     "visibleIf": "{Military Service 1/1} = 'Yes' or  ({Federal Civilian Employment 1/3}= 'No' and  {Determine Eligibility 1/3} = 'No')"
    }
   ],
   "name": "UCX and Out of State Claim",
   "title": "UCX and Out of State Claim",
   "visible": false,
   "visibleIf": "{Military Service 1/1} = 'Yes' or  ({Federal Civilian Employment 1/3}= 'No' and  {Determine Eligibility 1/3} = 'No')"
  },
  {
   "category": "Commuter",
   "elements": [
    {
     "type": "radiogroup",
     "name": "Commuter Questions 1/3",
     "tag": "Optimum RadioGroup",
     "title": "Did you recently relocate outside of the District of Columbia to your current address within the last two months?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Commuter Questions 2/3",
     "visible": false,
     "visibleIf": "{Commuter Questions 1/3} = 'No'",
     "tag": "Optimum RadioGroup",
     "title": "Did you commute on a regular basis into the District of Columbia",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Commuter Questions 3/3",
     "visible": false,
     "visibleIf": "{Commuter Questions 2/3} = 'Yes'",
     "tag": "Optimum RadioGroup",
     "title": "Do you plan to continue to commute into the District of Columbia to search for work? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    }
   ],
   "name": "Commuter",
   "title": "Commuter",
   "visible": false,
   "visibleIf": "{Determine Eligibility 3/3} = '20200'"
  },
  {
   "category": "Additional Information",
   "elements": [
    {
     "type": "panel",
     "elements": [
      {
       "type": "radiogroup",
       "name": "citizenshipInformation",
       "tag": "Optimum RadioGroup",
       "title": "Are you a United State citizen?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "panel",
       "elements": [
        {
         "type": "text",
         "isRequired": true,
         "name": "alienRegNumber",
         "tag": "Optimum SingleLine",
         "title": "If No, Please enter Alien Registration Number:"
        },
        {
         "type": "text",
         "inputType": "date",
         "isRequired": true,
         "name": "alienStatusExpirationDate",
         "tag": "Optimum Date",
         "title": "Alien Status Expiration Date"
        }
       ],
       "name": "panel4",
       "visible": false,
       "visibleIf": "{citizenshipInformation} = 'No'"
      }
     ],
     "name": "panel3"
    },
    {
     "type": "panel",
     "elements": [
      {
       "type": "radiogroup",
       "name": "veteranInformation",
       "tag": "Optimum RadioGroup",
       "title": "Are you a Veteran?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "panel",
       "elements": [
        {
         "type": "multipletext",
         "name": "veteranDate",
         "tag": "Optimum StartEndDatepicker",
         "startDateName": "If Yes, Enter Service Start Date",
         "endDateName": "If Yes, Enter Service EndDate",
         "title": "Veteran Start And End Date",
         "isRequired": true,
         "items": [
          {
           "name": "startDate"
          },
          {
           "name": "endDate"
          }
         ]
        },
        {
         "type": "dropdown",
         "choices": [
          "Honorable ",
          "General ",
          "Good ",
          "Satisfactory ",
          "Indifferent ",
          "Special Order ",
          "Medical ",
          "Dishonorable ",
          "Bad Conduct ",
          "Undesirable ",
          "Dismissal ",
          "Other than Honorable ",
          "Active Service Member "
         ],
         "isRequired": true,
         "name": "veteranDischargeType",
         "tag": "Optimum Dropdown",
         "title": "If Yes, Select Service Discharge Type:"
        }
       ],
       "name": "panel6",
       "visible": false,
       "visibleIf": "{veteranInformation} = 'Yes'"
      }
     ],
     "name": "panel5"
    }
   ],
   "name": "Additional Personal Information",
   "title": "Additional Personal Information"
  },
  {
   "category": "Additional Information",
   "elements": [
    {
     "type": "radiogroup",
     "name": "severancePayInformation",
     "tag": "Optimum RadioGroup",
     "title": "Are you or will you receive payment representing severance pay?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "elements": [
      {
       "type": "text",
       "isRequired": true,
       "name": "Additional Eligibility Question 2/14",
       "requiredErrorText": "Please enter the Severance amount.",
       "tag": "Optimum Currency",
       "title": "Enter the total amount in dollars and cents: "
      },
      {
       "type": "multipletext",
       "name": "SeverancPayDate",
       "tag": "Optimum StartEndDatepicker",
       "startDateName": "Start date of the severance pay period",
       "endDateName": "End date of the severance pay period",
       "title": "Please enter the Severance pay period. ",
       "isRequired": true,
       "items": [
        {
         "name": "startDate"
        },
        {
         "name": "endDate"
        }
       ]
      }
     ],
     "name": "panel7",
     "visible": false,
     "visibleIf": "{severancePayInformation} = 'Yes'"
    },
    {
     "type": "radiogroup",
     "name": "pensionInformation",
     "tag": "Optimum RadioGroup",
     "title": "Are you receiving, or have you applied for pension?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "inputType": "date",
     "isRequired": true,
     "name": "Additional Eligibility Question 6/14",
     "tag": "Optimum Date",
     "title": "If Yes, please enter the effective date of pension. ",
     "visible": false,
     "visibleIf": "{pensionInformation} = 'Yes'"
    },
    {
     "type": "radiogroup",
     "name": "childSupport",
     "tag": "Optimum RadioGroup",
     "title": "Are you required to pay child support??",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "elements": [
      {
       "type": "text",
       "isRequired": true,
       "name": "childSupportAmount",
       "placeHolder": "$ 1,000,000",
       "tag": "Optimum Currency",
       "title": "If Yes, Enter the monthly amount in dollars and cents:"
      },
      {
       "type": "dropdown",
       "choiceUrl": "optimum-clientside-library/platform-services/dist/data/statesList.json",
       "choices": [
        "item1",
        "item2",
        "item3"
       ],
       "isRequired": true,
       "name": "childSupportState",
       "tag": "Optimum Dropdown",
       "title": "If Yes, Enter the State"
      }
     ],
     "name": "panel8",
     "visible": false,
     "visibleIf": "{childSupport} = 'Yes'"
    },
    {
     "type": "radiogroup",
     "name": "Additional Eligibility Question 10/14",
     "tag": "Optimum RadioGroup",
     "title": "If you are paid benefits would you like us to withhold Federal income tax? (withholding is 10%)",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Additional Eligibility Question 11/14",
     "tag": "Optimum RadioGroup",
     "title": "If you are paid benefits, would you like us to withhold District of Columbia Income tax? (withholding is 5%)",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "filedUIInOtherState",
     "tag": "Optimum RadioGroup",
     "title": "Have you filed for Unemployment in any state other than The District of Columbia Since <<basePeriodBeginDate>> ?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "dropdown",
     "choiceUrl": "optimum-clientside-library/platform-services/dist/data/statesList.json",
     "isRequired": true,
     "name": "Additional Eligibility Question 13/14",
     "tag": "Optimum Dropdown",
     "title": "If Yes, please select the State where you have filed for Unemployment. ",
     "visible": false,
     "visibleIf": "{filedUIInOtherState} = 'Yes'"
    },
    {
     "type": "text",
     "inputType": "date",
     "isRequired": true,
     "name": "Additional Eligibility Question 14/14",
     "tag": "Optimum Date",
     "title": "If Yes, please enter the start date of the claim. ",
     "visible": false,
     "visibleIf": "{filedUIInOtherState} = 'Yes'"
    }
   ],
   "name": "Additional Eligibility Information",
   "title": "Additional Eligibility Information"
  },
  {
   "category": "Union/Recall Information",
   "elements": [
    {
     "type": "radiogroup",
     "name": "Union/Recall Information 1/5",
     "tag": "Optimum RadioGroup",
     "title": "Do you belong to a union? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Union/Recall Information 2/5",
     "visible": false,
     "visibleIf": "{Union/Recall Information 1/5} = 'Yes'",
     "tag": "Optimum RadioGroup",
     "title": "If yes, does your union have a hiring hall? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Union/Recall Information 3/5",
     "visible": false,
     "visibleIf": "{Union/Recall Information 2/5} = 'Yes",
     "tag": "Optimum RadioGroup",
     "title": "If yes, is it an exclusive hiring hall? ",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Union/Recall Information 4/5",
     "tag": "Optimum RadioGroup",
     "title": "Do you have a firm recall/rehire date to your most recent employer?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "text",
     "inputType": "date",
     "isRequired": true,
     "name": "Union/Recall Information 5/5",
     "tag": "Optimum Date",
     "title": "If yes, enter the recall/rehire date (Month DD, YYYY): ",
     "visible": false,
     "visibleIf": "{Union/Recall Information 4/5} = 'Yes'"
    }
   ],
   "name": "Union/Recall Information",
   "title": "Union/Recall Information",
   "visible": false,
   "visibleIf": "{Determine Eligibility 3/3} = '20100' or {Commuter Questions 3/3} = 'Yes'"
  },
  {
   "category": "Occupation Information",
   "elements": [
    {
     "type": "text",
     "name": "Job Title",
     "tag": "Optimum SingleLine",
     "title": "Job Title (required)"
    },
    {
     "type": "text",
     "name": "Job Description",
     "tag": "Optimum MultipleLine",
     "title": "Job Description (required)"
    }
   ],
   "name": "Occupation Information",
   "title": "Occupation Information"
  },
  {
   "category": "Standard Form 8/Standard Form 50",
   "elements": [
    {
     "type": "html",
     "name": "question3",
     "html": "To file an initial claim for unemployment compensation you should have at hand your Standard Form 8 (SF8) which is titled: \n</br>  \n\"NOTICE TO FEDERAL EMPLOYEE ABOUT UNEMPLOYMENT INSURANCE\" \n</br>\nor Standard Form 50 (SF50) which is titled:  \n</br>\n \"NOTIFICATION OF PERSONNEL ACTION\""
    },
    {
     "type": "panel",
     "elements": [
      {
       "type": "radiogroup",
       "name": "Federal Civilian Employment 2/3",
       "tag": "Optimum RadioGroup",
       "title": "Do you have at hand your Standard Form 8 or your Standard Form 50?",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "Federal Civilian Employment 3/3",
       "tag": "Optimum SingleLine",
       "title": "Please enter the 3-digit identification Federal Agency Code No. found in the middle of your SF8. If you do not have your Federal Agency Code No. located on your SF8, please enter your agency or employer name. If you enter your agency or employer name, the system will prompt you to select from a provided list."
      }
     ],
     "name": "panel1"
    }
   ],
   "name": "Standard Form 8/Standard Form 50",
   "title": "Standard Form 8/Standard Form 50",
   "visible": false,
   "visibleIf": "{Federal Civilian Employment 1/3} = 'Yes'"
  },
  {
   "category": "Standard Form 8/Standard Form 50",
   "elements": [
    {
     "type": "panel",
     "elements": [
      {
       "type": "html",
       "name": "question5",
       "html": "<center><b>It is important that you enter the employer for whom you last worked for at least 30 calendar days. </b></center>\n</br>\n<center><b>Failing to provide accurate employer information, including a valid mailing address, WILL result in a delay processing your claim. </b></center>"
      }
     ],
     "name": "panel2"
    },
    {
     "type": "paneldynamic",
     "name": "Federal Employer Inforamtion",
     "tag": "Optimum DynamicPanel",
     "title": "Federal Employer Inforamtion",
     "templateElements": [
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmployerName",
       "tag": "Optimum SingleLine",
       "title": "Employer Name (Enter Up to 30 Characters):"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmployerComponent",
       "tag": "Optimum SingleLine",
       "title": "Employer Component (Enter Up to 30 Characters):"
      },
      {
       "type": "multipletext",
       "name": "federalEmployerAddress",
       "tag": "Optimum Address",
       "title": "Address",
       "items": [
        {
         "name": "addressLine1",
         "isRequired": true
        },
        {
         "name": "addressLine2"
        },
        {
         "name": "city",
         "isRequired": true
        },
        {
         "name": "state",
         "isRequired": true
        },
        {
         "name": "zip",
         "isRequired": true
        },
        {
         "name": "zipExtension"
        }
       ]
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalStartDateEmployment",
       "tag": "Optimum Date",
       "title": "Start Date of Employment:"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEndDateEmployment",
       "tag": "Optimum Date",
       "title": "End Date of Employment:"
      },
      {
       "type": "dropdown",
       "choices": [
        "item1",
        "item2",
        "item3"
       ],
       "isRequired": true,
       "name": "federalSeperationReason",
       "tag": "Optimum Dropdown",
       "title": "Reason for Seperation:"
      },
      {
       "type": "text",
       "name": "federalPosition",
       "tag": "Optimum SingleLine",
       "title": "Position (Enter Up to 12 Characters):"
      },
      {
       "type": "text",
       "name": "federalTitle",
       "tag": "Optimum SingleLine",
       "title": "Title (Enter Up to 12 Characters):"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmploymentSiteCity",
       "tag": "Optimum SingleLine",
       "title": "Employment Site: City (Enter Upto 15 Characters);"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmploymentSiteState",
       "tag": "Optimum SingleLine",
       "title": "Employment Site: State(Enter 2 Characters);"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "federalEmploymentSiteCountry",
       "tag": "Optimum SingleLine",
       "title": "Employment Site: Country (Enter Upto 3 Characters);"
      },
      {
       "type": "radiogroup",
       "name": "federalFullTime",
       "tag": "Optimum RadioGroup",
       "title": "Full Time:",
       "isRequired": true,
       "choices": [
        "Yes",
        "No"
       ]
      },
      {
       "type": "html",
       "name": "question17",
       "html": "<center><b>To ensure your monetary determination is calculated properly to supply the name of all federal agencies you have worked for since April 1st 2017.</b></center>"
      }
     ],
     "panelCount": 1,
     "panelAddText": "Yes",
     "addPanelMessage": "Did you work for anyother agency of the federal government since {<<basePeriodBeginDate>>}?"
    }
   ],
   "name": "Federal Employer Information",
   "title": "Federal Employer Information",
   "visible": false,
   "visibleIf": "{Federal Civilian Employment 1/3} = 'Yes'"
  },
  {
   "category": "Earn wages in another State",
   "elements": [
    {
     "type": "radiogroup",
     "name": "Earn Wages in another State 1/3",
     "visible": false,
     "visibleIf": "{Determine Eligibility 2/3} = 'Yes' and {Federal Civilian Employment 1/3} = 'No'\n",
     "tag": "Optimum RadioGroup",
     "title": "Did you work and earn wages in another state between?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "radiogroup",
     "name": "Earn Wages in another State 2/3",
     "visible": false,
     "visibleIf": "{Determine Eligibility 2/3} = 'Yes' and {Federal Civilian Employment 1/3} = 'Yes'",
     "tag": "Optimum RadioGroup",
     "title": "Did you work in any state(s) other than the District of Columbia for a non-federal government agency between <<basePeriodBeginDate>> 12: 00:00 AM and  <<basePeriodEndDate>> 12: 00:00 AM ?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "dropdown",
     "choiceUrl": "optimum-clientside-library/platform-services/dist/data/statesList.json",
     "isRequired": true,
     "name": "Earn Wages in another State 3/3",
     "tag": "Optimum Chosen",
     "title": "If you answered yes, please select all the states you have worked and earned wages in between the dates shown above.",
     "visible": false,
     "visibleIf": "{Earn Wages in another State 1/3} = 'Yes' or {Earn Wages in another State 2/3} = 'Yes'"
    }
   ],
   "name": "Earn wages in another State",
   "title": "Earn wages in another State",
   "visible": false,
   "visibleIf": "{Determine Eligibility 2/3} = 'Yes' "
  },
  {
   "category": "Employer Information",
   "elements": [
    {
     "type": "dropdown",
     "choiceUrl": "optimum-api/claimant-service/api/claimant/employerHistory/byClaimantId/<<claimantId>>",
     "isRequired": true,
     "name": "DCLookupService",
     "otherText": "EMPLOYER NOT LSITED",
     "tag": "Optimum Chosen",
     "title": "Please select the employer for which you most recently worked for a continous 30-day period:\n\nIt is important that you select your correct seperating employer. Failing to provide accurate employer information, including a valid mailing address, Will result in a delay processing your claim.",
     "visibleIf": "<<wagesPresent>> = 'true'"
    },
    {
     "type": "radiogroup",
     "name": "workedNonFederalEmployer",
     "visibleIf": "({Federal Civilian Employment 1/3} = 'Yes'  and <<wagesPresent>> = 'false') or ({Federal Civilian Employment 1/3} = 'Yes'  and <<wagesPresent>> = 'true' and {DCLookupService} notEmpty and {DCLookupService} ='EMPLOYER NOT LISTED')",
     "tag": "Optimum RadioGroup",
     "title": "Have you worked for any non federal employer since your separation with {federalEmployerName} {federalEndDateEmployment} ?",
     "isRequired": true,
     "choices": [
      "Yes",
      "No"
     ]
    },
    {
     "type": "panel",
     "elements": [
      {
       "type": "html",
       "name": "question4",
       "html": "<b>It is important that you enter the employer for whom you last worked for at least 30 calendar days.</b>\n<br>\n<b>Failing to provide accurate employer information, including a valid mailing address, WILL result in a delay processing your claim.</b>"
      },
      {
       "type": "text",
       "isRequired": true,
       "name": "employerName",
       "tag": "Optimum SingleLine",
       "title": "Employer Name (Enter up to 30 characters):"
      },
      {
       "type": "multipletext",
